            assembly {
                _bytes32 := mload(add(byteArray, k))
            }
            if (k <= numBytes32 * 32) {
                bytes32Array[k.sub(32).div(32)] = _bytes32;
            } else {
                remainder[k.sub(96).div(32)] = _bytes32;
            }
        }
    }
}
